  deleteContact = (contact) => {
    this.setState((currentState) => ({
      contacts: currentState.contacts.filter((c) => {
        return c.id !== contact.id
      })
    }))
  }
  render() {
    return (
      <div >
        <ListContacts contacts={this.state.contacts} onclickDelete={this.deleteContact} />
      </div>
    )

  }
}
export default App
