import React, { Component } from 'react'
import ListContacts from './listContact'
import * as ContactsApi from './utils/ContactsAPI'
import CreateContact from './createContact'

import {Route} from 'react-router-dom'

class App extends Component {
  state={
    contacts : [],
    screen:'list'

  }
  componentDidMount(){
    ContactsApi.getAll().then((contacts) => this.setState(()=>({contacts})))
  }

deleteContact = (contact) => {
    this.setState((currentState) => ({
      contacts: currentState.contacts.filter((c) => {
        return c.id !== contact.id
      })
    }))
    ContactsApi.remove(contact)
  }
  render() {
    return (
      <div >
        <Route path='/' render={()=>(<ListContacts onNavigate={()=>{
         this.setState(()=>({
           screen:'create'
         }))
       }} contacts={this.state.contacts} onclickDelete={this.deleteContact} />)}/>

      {this.state.screen==='create' && (<CreateContact></CreateContact>)}
      </div>
    )

  }

}
export default App
